loadstring("local Players=game:GetService('Players') local VirtualUser=game:GetService('VirtualUser') local UserInputService=game:GetService('UserInputService') local RunService=game:GetService('RunService') local Player=Players.LocalPlayer if not UserInputService.TouchEnabled then return end getgenv().AutoClick=false getgenv().GravityOn=false getgenv().InfiniteJumpEnabled=false getgenv().ESPEnabled=false getgenv().FOVOn=false getgenv().OriginalFOV=workspace.CurrentCamera.FieldOfView getgenv().CustomFOV=120 local Gui=Instance.new('ScreenGui',game:GetService('CoreGui')) Gui.Name='MobileHub' getgenv().MobileHub=Gui local ToggleBtn=Instance.new('TextButton',Gui) ToggleBtn.Size=UDim2.new(0,120,0,40) ToggleBtn.Position=UDim2.new(0,10,0,70) ToggleBtn.Text='OPEN HUB' ToggleBtn.Visible=true ToggleBtn.Active=true ToggleBtn.Draggable=true ToggleBtn.BackgroundColor3=Color3.fromRGB(35,35,35) ToggleBtn.TextColor3=Color3.new(1,1,1) Instance.new('UICorner',ToggleBtn).CornerRadius=UDim.new(0,10) local Frame=Instance.new('Frame',Gui) Frame.Size=UDim2.new(0,220,0,280) Frame.Position=UDim2.new(0,10,0,120) Frame.Visible=false Frame.Active=true Frame.Draggable=true Frame.BackgroundColor3=Color3.fromRGB(25,25,25) Instance.new('UICorner',Frame).CornerRadius=UDim.new(0,12) local AutoBtn=Instance.new('TextButton',Frame) AutoBtn.Size=UDim2.new(1,-20,0,40) AutoBtn.Position=UDim2.new(0,10,0,10) AutoBtn.Text='AUTO CLICK : OFF' AutoBtn.TextColor3=Color3.new(1,1,1) AutoBtn.BackgroundColor3=Color3.fromRGB(45,45,45) Instance.new('UICorner',AutoBtn).CornerRadius=UDim.new(0,10) local GravityBtn=Instance.new('TextButton',Frame) GravityBtn.Size=UDim2.new(1,-20,0,40) GravityBtn.Position=UDim2.new(0,10,0,60) GravityBtn.Text='GRAVITY : OFF' GravityBtn.TextColor3=Color3.new(1,1,1) GravityBtn.BackgroundColor3=Color3.fromRGB(45,45,45) Instance.new('UICorner',GravityBtn).CornerRadius=UDim.new(0,10) local JumpBtn=Instance.new('TextButton',Frame) JumpBtn.Size=UDim2.new(1,-20,0,40) JumpBtn.Position=UDim2.new(0,10,0,110) JumpBtn.Text='INFINITE JUMP : OFF' JumpBtn.TextColor3=Color3.new(1,1,1) JumpBtn.BackgroundColor3=Color3.fromRGB(45,45,45) Instance.new('UICorner',JumpBtn).CornerRadius=UDim.new(0,10) local ESPBtn=Instance.new('TextButton',Frame) ESPBtn.Size=UDim2.new(1,-20,0,40) ESPBtn.Position=UDim2.new(0,10,0,160) ESPBtn.Text='ESP : OFF' ESPBtn.TextColor3=Color3.new(1,1,1) ESPBtn.BackgroundColor3=Color3.fromRGB(45,45,45) Instance.new('UICorner',ESPBtn).CornerRadius=UDim.new(0,10) local FOVBtn=Instance.new('TextButton',Frame) FOVBtn.Size=UDim2.new(1,-20,0,40) FOVBtn.Position=UDim2.new(0,10,0,210) FOVBtn.Text='FOV : OFF' FOVBtn.TextColor3=Color3.new(1,1,1) FOVBtn.BackgroundColor3=Color3.fromRGB(45,45,45) Instance.new('UICorner',FOVBtn).CornerRadius=UDim.new(0,10) ToggleBtn.MouseButton1Click:Connect(function() Frame.Visible=not Frame.Visible ToggleBtn.Text=Frame.Visible and 'CLOSE HUB' or 'OPEN HUB' end) task.spawn(function() while true do if getgenv().AutoClick then VirtualUser:Button1Down(Vector2.new(),workspace.CurrentCamera.CFrame) VirtualUser:Button1Up(Vector2.new(),workspace.CurrentCamera.CFrame) end task.wait(0.1) end end) AutoBtn.MouseButton1Click:Connect(function() getgenv().AutoClick=not getgenv().AutoClick AutoBtn.Text='AUTO CLICK : '..(getgenv().AutoClick and 'ON' or 'OFF') end) GravityBtn.MouseButton1Click:Connect(function() getgenv().GravityOn=not getgenv().GravityOn workspace.Gravity=getgenv().GravityOn and 50 or 196.2 GravityBtn.Text='GRAVITY : '..(getgenv().GravityOn and 'ON' or 'OFF') end) JumpBtn.MouseButton1Click:Connect(function() getgenv().InfiniteJumpEnabled=not getgenv().InfiniteJumpEnabled JumpBtn.Text='INFINITE JUMP : '..(getgenv().InfiniteJumpEnabled and 'ON' or 'OFF') end) ESPBtn.MouseButton1Click:Connect(function() getgenv().ESPEnabled=not getgenv().ESPEnabled ESPBtn.Text='ESP : '..(getgenv().ESPEnabled and 'ON' or 'OFF') end) FOVBtn.MouseButton1Click:Connect(function() getgenv().FOVOn=not getgenv().FOVOn workspace.CurrentCamera.FieldOfView=getgenv().FOVOn and getgenv().CustomFOV or getgenv().OriginalFOV FOVBtn.Text='FOV : '..(getgenv().FOVOn and 'ON' or 'OFF') end) UserInputService.JumpRequest:Connect(function() if getgenv().InfiniteJumpEnabled and Player.Character and Player.Character:FindFirstChildOfClass('Humanoid') then Player.Character:FindFirstChildOfClass('Humanoid'):ChangeState(Enum.HumanoidStateType.Jumping) end end)")()
